import React, { useState } from "react";
import filterCars from './filterCars';

    function CategoryBar({ categoryList, setFilteredCars }) {
    const [selectedSubCategory, setSelectedSubCategory] = useState("");

    const filterItems = (subcategory) => {
    setSelectedSubCategory(subcategory);
    const filteredCars = filterCars("", subcategory, categoryList);
    setFilteredCars(filteredCars);
    };

    return (
    <div className="flex-container">
        {categoryList.map((category, index) => (
        <div key={index}>
            <div className="dropdown">
            <button className="dropbtn">{category.categoryName}</button>
            <div className="dropdown-content">
                {category.subcategories.map((subcategory, subIndex) => (
                <div
                    className="dropdown-content-item"
                    onClick={() => filterItems(subcategory)}
                    key={subIndex}
                >
                    {subcategory}
                </div>
                ))}
            </div>
            </div>
        </div>
        ))}
    </div>
    );
    }

export default CategoryBar;
