import React, { useState, useEffect } from "react";
import axios from "axios";
import CarBrowsing from './CarBrowsing';

function CategoryBar({ categoryList }) {
  const [selectedCategory, setSelectedCategory] = useState("");
  const [selectedSubCategory, setSelectedSubCategory] = useState("");
  const [cars, setCars] = useState([]);
  const [filteredCars, setFilteredCars] = useState([]);

  useEffect(() => {
    axios
      .get("http://localhost:3001/cars.json")
      .then((response) => {
        setCars(response.data.cars);
      })
      .catch((error) => {
        console.error(error);
      });
  }, []);

  const filterCars = (subcategory) => {
    if (selectedCategory != "") {
      return filteredCars.filter(
        (car) => car.categoryName === selectedCategory && car.seats === subcategory
      );
    }
  };

  const filterItems = (subcategory) => {
    setSelectedSubCategory(subcategory);
    setFilteredCars(filterCars(subcategory));
  };

  const handleCategoryChange = (category) => {
    setSelectedCategory(category);
    setFilteredCars(filterCars(selectedSubCategory));
  };

  return (
    <div className="flex-container">
      {categoryList.map((category, index) => (
        <div key={index}>
          <div className="dropdown">
            <button className="dropbtn">{category.categoryName}</button>
            <div className="dropdown-content">
              {category.subcategories.map((subcategory, subIndex) => (
                <div
                  className="dropdown-content-item"
                  onClick={() => filterItems(subcategory)}
                  key={subIndex}
                >
                  {subcategory}
                </div>
              ))}
            </div>
          </div>
        </div>
      ))}

      <div>
        <select
          value={selectedSubCategory}
          onChange={(e) => setSelectedSubCategory(e.target.value)}
        >
          <option value="">All Subcategories</option>
          {categoryList
            .find((category) => category.categoryName === selectedCategory)
            ?.subcategories.map((subcategory, index) => (
              <option value={subcategory} key={index}>
                {subcategory}
              </option>
            ))}
        </select>
      </div>
      <div>
        {filteredCars.map((car) => (
          <div key={car.id}>{car.name}</div>
        ))}
      </div>
    </div>
  );
}

export default CategoryBar;
