import React, { useState, useEffect } from "react";
import axios from "axios";

function filterCars({
    lastId, setCartItems, cartItems, setTotalPrice, totalPrice, setIsCartEmpty, setLastId, isCartEmpty
}) {
const [error, setError] = useState(null);
const [isLoaded, setIsLoaded] = useState(false);
const [cars, setCars] = useState([]);
const [selectedCategory, setSelectedCategory] = useState("");
const [filteredCars, setFilteredCars] = useState([]);

useEffect(() => {
    axios
    .get("http://localhost:3001/cars.json")
    .then((response) => {
        setIsLoaded(true);
        setCars(response.data.cars);
        setFilteredCars(response.data.cars);
    })
    .catch((error) => {
        setIsLoaded(true);
        setError(error);
    });
}, []);

    const handleCategoryChange = (event) => {
        const selectedCategory = event.target.value;
        setSelectedCategory(selectedCategory);

        if (selectedCategory === "") {
        setFilteredCars(cars);
        } else {
        const filteredCars = cars.filter((car) => car.seats === selectedCategory);
        setFilteredCars(filteredCars);
        }
    };

if (error) {
return <div>Error: {error.message}</div>;
} else if (!isLoaded) {
return <div>Loading...</div>;
} else {
return (
   <handleCategoryChange/>
);
}
}

export default filterCars;
