import React, { useState } from "react";
import filterCars from "./filterCars";
import categoryList from "/Users/net/car-rental-system/clint/src/components/categoryList.js"
import CarBrowsing from './CarBrowsing';


function CategoryBar({ categoryList }) {
    const [selectedCategory, setSelectedCategory] = useState("");
    const [selectedSubCategory, setSelectedSubCategory] = useState("");

    const updateSelectedItem = (category, selectedSubCategory) => {
        setSelectedCategory()
        setSelectedSubCategory()
        const filteredCars = filterCars(selectedCategory, selectedSubCategory, CarBrowsing.cars);
        CarBrowsing.setCars(filteredCars);
    };

    return (
        <div className="flex-container">
            {categoryList && categoryList.map((category, index) => (
                <div key={index}>
                    <div className="dropdown">
                        <button className="dropbtn">{category.categoryName}</button>
                        <div className="dropdown-content">
                            {category.subcategories && category.subcategories.map((subcategory, subIndex) => (
                                <div
                                    className="dropdown-content-item"
                                    onClick={() => updateSelectedItem(category.categoryName, subcategory)}
                                    key={subIndex}
                                >
                                    {subcategory}
                                </div>
                            ))}
                        </div>
                    </div>
                </div>
            ))}
        </div>
    );
}

export default CategoryBar;
