import React, { useState, useEffect } from "react";
import axios from "axios";

function FilterCars({ setSelectedCategory, setSelectedSubCategory, setFilteredCars }) {
  const [error, setError] = useState(null);
  const [isLoaded, setIsLoaded] = useState(false);
  const [cars, setCars] = useState([]);

  useEffect(() => {
    axios
      .get("http://localhost:3001/cars.json")
      .then((response) => {
        setIsLoaded(true);
        setCars(response.data.cars);
        setFilteredCars(response.data.cars);
      })
      .catch((error) => {
        setIsLoaded(true);
        setError(error);
      });
  }, []);

  const handleCategoryChange = (event) => {
    const selectedCategory = event.target.value;
    setSelectedCategory(selectedCategory);
    setSelectedSubCategory("");

    if (selectedCategory === "") {
      setFilteredCars(cars);
    } else {
      const filteredCars = cars.filter(
        (car) => car.category === selectedCategory && car.seats === ""
      );
      setFilteredCars(filteredCars);
    }
  };

  if (error) {
    return <div>Error: {error.message}</div>;
  } else if (!isLoaded) {
    return <div>Loading...</div>;
  } else {
    return (
      <div>
        <select onChange={handleCategoryChange}>
          <option value="">All Categories</option>
          {cars.map((car) => (
            <option key={car.id} value={car.category}>
              {car.category}
            </option>
          ))}
        </select>
      </div>
    );
  }
}

export default FilterCars;
