import logo from "/Users/net/car-rental-system/src/assets/images/logo.png";
import "./App.css";
import React, { useState, useRef } from "react";
import itemList from "/Users/net/car-rental-system/src/components/itemList.js";

// Web structure & information:
// 1. Frontend: React.js
// 2. Background: Node.js
// 3. Database: mySQL2 & Axios
// 4. Git: github
// 5. Send Email: EmailJS


function App() {
    const [data, setData] = useState(itemList)
    const [popup, setPopup] = useState(false)
    const [selectedItem, setSelectedItem] = useState(null);
    const [cartItems, setCartItems] = useState([]);
    const [totalPrice, setTotalPrice] = useState(0); 
    const [isCartEmpty, setIsCartEmpty] = useState(true);
    const [showForm, setShowForm] = useState(false);
    const [searchTerm, setSearchTerm] = useState("");
    const [cartPopup, setCartPopup] = useState(false);
    const [searchResults, setSearchResults] = useState([]);

        const [name, setName] = useState("");
        const [address, setAddress] = useState("");
        const [suburb, setSuburb] = useState("");
        const [state, setState] = useState("");
        const [country, setCountry] = useState("");
        const [phone, setPhone] = useState("");
        const [email, setEmail] = useState("");
        const [formError, setFormError] = useState(false);


    const filterItems = (Item) => {
        const result = itemList.filter((curData)=>{
            return curData.subcategory===Item; 
        })
        setData(result);
    };

    const handleSearchChange = (event) => {
        setSearchTerm(event.target.value);
    };


    const handleSearch = (e) => {
        e.preventDefault();
        const result = itemList.filter((item) => {
            return item.name.includes(searchTerm);
            })
        setSearchResults(result);
    };

    const togglePopup = (item) => {
        setSelectedItem(item);
        setPopup(!popup);
    }

    const toggleCartPopup = () => {
        setCartPopup(!cartPopup);
    }

    const closeSearchResultsPopup = () => {
        setSearchResults([]);
    }

    const addToCart = (item) => {
        if (item.quantity > 0){
        setCartItems([...cartItems, item]);
        setTotalPrice(totalPrice + item.price);
        setIsCartEmpty(false);
        Axios.post("http://localhost:3001/order", {
            productName: item.name,
            price: item.price,
                }).then(() => {
                console.log("success");
        });
    }
    }

    const removeItem = (item) => {
        const itemIndex = cartItems.findIndex((i) => i === item);
        if (itemIndex >= 0) {
        const updatedItems = [...cartItems];
        updatedItems.splice(itemIndex, 1);

        setCartItems(updatedItems);
        setTotalPrice(totalPrice - item.price);
        setIsCartEmpty(updatedItems.length === 0);
        }
        };

    const clearCart = () => {
        setCartItems([]);
        setTotalPrice(0);
        setIsCartEmpty(true);
    };

    const checkout = (item) => {
        if (!isCartEmpty) {
        setShowForm(!showForm);
        setCartPopup(false);
        setCartItems([]);
        }
    };
    const form = useRef();
        const sendEmail = () => {
          emailjs.sendForm('service_nfkk39e', 'template_yrcn2nu', form.current, 'bUd9ImW35roV2ZpeL')
            .then((result) => {
                console.log(result.text);
            }, (error) => {
                console.log(error.text);
            });
        };


    const placeOrder = (event) => {
        event.preventDefault();
    if (name && address && suburb && state && country && phone && email !== " ") {
        setShowForm(false);
        setCartItems([]);
        setIsCartEmpty(true);
        setFormError(true);
        sendEmail()

        Axios.post("http://localhost:3001/create", {
        name: name,
        address: address,
        suburb: suburb,
        state: state,
        country: country,
        phone: phone,
        email: email,
            }).then(() => {
            console.log("success");
            });
        };
    // Handle form submission here, e.g. submit data to a server or show a confirmation message
        console.log("Form submitted", name, address, suburb, state, country, phone, email);
        setShowForm(false);
        setName("");
        setAddress("");
        setSuburb("");
        setState("");
        setCountry("");
        setPhone("");
        setEmail("");
        setFormError(false);
        alert("Order placed successfully!");
  };

    return (
    <>
    {/* NavBar */}
    <nav class='navBar-setting flex-container navBar-menu-text'>
        <img class='logo' src={logo} alt='Logo'/>
    {/* Category */}
        <div class="dropdown">
            <button class="dropbtn">Frozen Food</button>
    {/* Subcategory */}
            <div class="dropdown-content">
                <div className="dropdown-content-item" onClick={() => filterItems('Frozen Vegetables')}>Frozen Vegetables</div>
                <div className="dropdown-content-item" onClick={() => filterItems('Frozen Fruits')}>Frozen Fruits</div>   
                <div className="dropdown-content-item" onClick={() => filterItems('Frozen Seafood')}>Frozen Seafood</div>       
            </div>
        </div>
        <div class="dropdown">
            <button class="dropbtn">Fresh Food</button>
            <div class="dropdown-content">
                <div className="dropdown-content-item" onClick={() => filterItems('Fresh Fruits')}>Fresh Fruits</div>
                <div className="dropdown-content-item" onClick={() => filterItems('Fresh Vegetables')}>Fresh Vegetables</div>   
                <div className="dropdown-content-item" onClick={() => filterItems('Fresh Seafood')}>Fresh Seafood</div>       
            </div>
        </div>
        <div class="dropdown">
            <button class="dropbtn">Beverages</button>
            <div class="dropdown-content">
                <div className="dropdown-content-item" onClick={() => filterItems('Milk')}>Milk</div>
                <div className="dropdown-content-item" onClick={() => filterItems('Coffee')}>Coffee</div>   
                <div className="dropdown-content-item" onClick={() => filterItems('Juices')}>Juices</div>   
                <div className="dropdown-content-item" onClick={() => filterItems('Energy drink')}>Energy drink</div>       
            </div>
        </div>
        <div class="dropdown">
            <button class="dropbtn">Home Health</button>
            <div class="dropdown-content">
                <div className="dropdown-content-item" onClick={() => filterItems('Vitamin')}>Vitamin</div>      
            </div>
        </div>
        <div class="dropdown">
            <button class="dropbtn">Pet Food</button>
            <div class="dropdown-content">
                <div className="dropdown-content-item" onClick={() => filterItems('Cat Food')}>Cat Food</div>
                <div className="dropdown-content-item" onClick={() => filterItems('Dog Food')}>Dog Food</div>
                <div className="dropdown-content-item" onClick={() => filterItems('Fish Food')}>Fish Food</div>         
            </div>
        </div>

    {/* SearchBar */}
        <div class="searchBar">
            <form onSubmit={handleSearch}>
                <input
                    id="searchTerm"
                    type="text"
                    placeholder="Search..."
                    value={searchTerm}
                    onChange={handleSearchChange}
                />
                <button type="submit">Search</button>
                    {/* <label for="price-filter">Filter by price:</label> */}
                    {/* <label htmlFor="minPrice">Minimum Price:</label>
                    <input type="text" id="minPrice" value={minPrice} onChange={(e) => setMinPrice(e.target.value)} />

                    <label htmlFor="maxPrice">Maximum Price:</label>
                    <input type="text" id="maxPrice" value={maxPrice} onChange={(e) => setMaxPrice(e.target.value)} /> */}
            </form>
        </div>
    </nav>

    <body>
    <div class="background-main flex-container">

     {/* side-menu */}
     <div className="shoppingCart-container">
        <div className="side-menu">
                <div className="sideMenu-setting">
                    <div onClick={() => filterItems('Frozen Vegetables')}>Frozen Vegetables</div>
                    <div onClick={() => filterItems('Frozen Fruits')}>Frozen Fruits</div>   
                    <div onClick={() => filterItems('Frozen Seafood')}>Frozen Seafood</div>   
                    <div onClick={() => filterItems('Fresh Fruits')}>Fresh Fruits</div>
                    <div onClick={() => filterItems('Fresh Vegetables')}>Fresh Vegetables</div>   
                    <div onClick={() => filterItems('Fresh Seafood')}>Fresh Seafood</div>       
                    <div onClick={() => filterItems('Milk')}>Milk</div>
                    <div onClick={() => filterItems('Coffee')}>Coffee</div>   
                    <div onClick={() => filterItems('Juices')}>Juices</div>   
                    <div onClick={() => filterItems('Energy drink')}>Energy drink</div>
                    <div onClick={() => filterItems('Cat Food')}>Cat Food</div>
                    <div onClick={() => filterItems('Dog Food')}>Dog Food</div>
                    <div onClick={() => filterItems('Fish Food')}>Fish Food</div>   
                </div>
            </div>
     </div>

    {/* search-result */}
    <div className="search-result-container">
        <div class="flex-container mainArea-card-container">
        { searchResults.map((item) => (
            <div className="popup">
                <div className="overlay"></div>
                <div className="popup-content">
                    <button className="close-popup" onClick={closeSearchResultsPopup} >Close</button>
                    <img className= "popup-img" src={item.image}></img>
                    <h2 className="mainArea-title">{item.name}</h2>
                    <p className="description">{item.description}</p>
                    <p className="mainArea-amount">{item.quantity > 0 ? "In stock" : <p className="cart-item-card-outOfStock">Out of stock</p>}</p>
                    <p className="mainArea-price">Price: ${item.price} AU</p>                    
                    <button className="addToCartBtn" onClick={() => addToCart(item)}>Add to Cart</button>
                </div>
            </div>
        ))}
    </div>
    </div>

     {/* main-area */}
        <div class="main-area">
            <div class="flex-container mainArea-card-container">
                {data.map((values) => {
                    const {id,name,price,image,quantity}=values;
                    return(
                        <>
                        <div className="mainArea-card" key={id}>
                            <img onClick={() => togglePopup(values)} className= "mainArea-img" src={image}></img>
                            <h3 onClick={() => togglePopup(values)} className="mainArea-title">{name}</h3>
                            <p onClick={() => togglePopup(values)} className="mainArea-amount">{quantity > 0 ? "In stock" : <p className="cart-item-card-outOfStock">Out of stock</p>}</p>
                            <p onClick={() => togglePopup(values)} className="mainArea-price"> Price: ${price} AU</p>                    
                            <button className="addToCartBtn" onClick={() => addToCart(values)}>Add to Cart</button>
                        </div>
                        {/* item-popup */}
                            {popup && selectedItem &&(
                            <div className="popup">
                                <div className="overlay"></div>
                                <div className="popup-content">
                                    <button className="close-popup" onClick={togglePopup}>Close</button>
                                    <img className= "popup-img" src={selectedItem.image}></img>
                                    <h2 className="mainArea-title">{selectedItem.name}</h2>
                                    <p className="description">{selectedItem.description}</p>
                                    <p className="mainArea-amount">{selectedItem.quantity > 0 ? "In stock" : <p className="cart-item-card-outOfStock">Out of stock</p>}</p>
                                    <p className="mainArea-price">Price: ${selectedItem.price} AU</p>                    
                                    <button className="addToCartBtn" onClick={() => addToCart(values)}>Add to Cart</button>
                                </div>
                            </div>
                            )}
                        </>
                        )
                    })}
            </div>
        </div>

    {/* shoppingCart */}
    <div class="shoppingCart" onClick={toggleCartPopup}>
        <img class='cart_icon' src={cart_icon} alt='cart_icon'/></div>
    </div>

    {/* cart-popup */}
    {cartPopup &&(
    <div className="popup-cart">
        <div className="overlay"></div>
        <div className="cart-container">
        <div className="cart-row">
            <div className="cart-content">
                <div className="cart-header">
                    <h2 className="cart-title">You have {cartItems.length} items in the cart.</h2>
                    <button className="close-popup" onClick={toggleCartPopup}>Close</button>
                </div>
                <div className="cart-card-container">
                        {cartItems.length === 0 ? (
                            <p>Your cart is empty</p>
                        ) : (
                        <ul className="cart-item-card-container">
                            {cartItems.map((item) => (
                            <li className="cart-item-card" key={item.id}>
                                <div className="">{item.name} - ${item.price} x 1</div>
                                <button className="mainArea-price" onClick={() => removeItem(item)}>Remove</button>
                            </li>
                            ))}
                        </ul>
                        )}
                    <div className="cart-footer">
                            <h3>Total Price: ${totalPrice.toFixed(2)}</h3>
                            <button onClick={clearCart}>Clear Cart</button>
                            <button onClick={checkout}  disabled={isCartEmpty} style={{ backgroundColor: isCartEmpty ? "grey" : "white" }}>Checkout</button>
                    </div>
                </div>
            </div>
        </div>
        </div>
    </div>
    )}

    {/* orderForm-popup */}
    {showForm && (
        <div className="pop-form">
            <div className="overlay"></div>
                <div className="form-container">
                    <div className="form-content">
                    <form ref={form} className="order-form" onSubmit={placeOrder}>
                    <h2>Order Form</h2>
                    <button className="close-popup" onClick={checkout}>Close</button>
                    {formError && <p className="error-message">Please fill in all fields.</p>}
                    <div>
                    <label className="order-form-item" htmlFor="name">Name:</label>
                        <input
                            type="text"
                            name="name"
                            id="name"
                            value={name}
                            onChange={(event) => setName(event.target.value)}
                        />
                    </div>

                    <div>
                        <label className="order-form-item">Address:</label>
                            <input
                                type="text"
                                name="address"
                                id="address"
                                value={address}
                                onChange={(event) => setAddress(event.target.value)}
                            />
                    </div>

                    <div>
                        <label className="order-form-item">Suburb:</label>
                            <input
                                type="text"
                                name="suburb"
                                id="suburb"
                                value={suburb}
                                onChange={(event) => setSuburb(event.target.value)}
                            />
                    </div>

                    <div>
                        <label className="order-form-item">State:</label>
                            <input
                                type="text"
                                name="state"
                                id="state"
                                value={state}
                                onChange={(event) => setState(event.target.value)}
                            />
                    </div>

                    <div>
                        <label className="order-form-item">Country:</label>
                            <input
                                type="text"
                                name="country"
                                id="country"
                                value={country}
                                onChange={(event) => setCountry(event.target.value)}
                            />
                    </div>

                    <div>
                        <label className="order-form-item">Phone:</label>
                            <input
                                type="tel"
                                name="phone"
                                id="phone"
                                value={phone}
                                onChange={(event) => setPhone(event.target.value)}
                            />
                    </div>

                    <div>
                        <label className="order-form-item">Email:</label>
                            <input
                                type="email"
                                name="email"
                                id="email"
                                value={email}
                                onChange={(event) => setEmail(event.target.value)}
                            />
                    </div>
                    <div className="order-form-footer-btn">
                        <button type="submit" onClick={placeOrder}>Place Order</button>
                    </div>
                    </form>
                </div>
            </div>
        </div>
    )}
</body>
</>
)
}
export default App
