import logo from "/Users/net/car-rental-system/src/assets/images/logo.png";
import "./App.css";
import React, { useState, useEffect } from "react";
import axios from "axios";

function App() {
    const [cartItems, setCartItems] = useState([]);
    const [totalPrice, setTotalPrice] = useState(0); 
    const [isCartEmpty, setIsCartEmpty] = useState(true);
    const [showForm, setShowForm] = useState(false);
    const [cartPopup, setCartPopup] = useState(false);
    const [reservationDays, setReservationDays] = useState(1);
    const [lastId, setLastId] = useState(0);


        const [firstName, setFirstName] = useState("");
        const [lastName, setLastName] = useState("");
        const [address, setAddress] = useState("");
        const [suburb, setSuburb] = useState("");
        const [city, setCity] = useState("");
        const [state, setState] = useState("");
        const [country, setCountry] = useState("");
        const [postCode, setPostCode] = useState("");
        const [master, setMaster] = useState("");
        const [visa, setVisa] = useState("");
        const [email, setEmail] = useState("");
        const [formError, setFormError] = useState(false);


    const handleChange = (e) => {
        setReservationDays(e.target.value); 
    };

    const addToCart = (item) => {
    if (item.availability === "True") {
        const newCartItem = {
        ...item,
        id: lastId + 1,
        };
        setCartItems([...cartItems,{ ...newCartItem, reservationDays: 1 }]);
        setTotalPrice(totalPrice + item.price_per_day);
        setIsCartEmpty(false);
        setLastId(lastId + 1);
        console.log(cartItems)
    }
    };


const tableRows = cartItems.map((item) => {
    const handleChange = (e) => {
        const updatedCart = cartItems.map((cartItem) => {
        if (cartItem.id === item.id) {
            return { ...cartItem, reservationDays: e.target.value };
        }
        return cartItem;
        });
        setCartItems(updatedCart);
    };

    return (
        <tr key={item.id}>
        <td className="cart_table_item">
            <img src={item.image} alt={item.name} height="50" width="80" />
        </td>
        <td className="cart_table_item">{item.name}</td>
        <td className="cart_table_item">$ {item.price_per_day}</td>
        <td className="cart_table_item">
            <input type="number" min="1" value={item.reservationDays || 1} onChange={handleChange} />
        </td>
        <td className="cart_table_item">
            <button onClick={() => removeItem(item)}>Remove</button>
        </td>
        </tr>
    );
    });

    const toggleCartPopup = () => {
        setCartPopup(!cartPopup);
    }

    const removeItem = (item) => {
        const itemIndex = cartItems.findIndex((i) => i === item);
        if (itemIndex >= 0) {
        const updatedItems = [...cartItems];
        updatedItems.splice(itemIndex, 1);

        setCartItems(updatedItems);
        setTotalPrice(totalPrice - item.price_per_day);
        setIsCartEmpty(updatedItems.length === 0);
        }
        };

    const checkout = (item) => {
        if (!isCartEmpty) {
        setShowForm(!showForm);
        setCartPopup(false);
        setTotalPrice(
            cartItems.reduce((total, item) => total + item.price_per_day * item.reservationDays, 0)
        );
        setCartItems([]);
        }
        console.log(cartItems);
    };

    const placeOrder = (event) => {
        event.preventDefault();
    if (firstName && lastName && address && suburb && state && country && master && visa && email !== " ") {
        setShowForm(false);
        setCartItems([]);
        setIsCartEmpty(true);
        setFormError(true);

    // Handle form submission here, e.g. submit data to a server or show a confirmation message
        setShowForm(false);
        setFirstName("");
        setLastName("");
        setAddress("");
        setSuburb("");
        setState("");
        setCountry("");
        setEmail("");
        setFormError(false);
        alert("Order placed successfully!");
  };
};

    return (
    <>
    {/* NavBar */}
    <nav className='navBar-setting navBar-menu-text nav-container'>
        <div className="nav-container-item"><img className='logo' src={logo} alt='Logo'/></div>
        <div className="shoppingCart-item"><h1>Car Rental Center</h1></div> 
        <botton className="reservation-btn" onClick={toggleCartPopup}>Car Reservation ({cartItems.length})</botton>
    </nav>

    <body>
     {/* main-area */}
      <MyComponent/>

    {/* cart-popup */}
    {cartPopup &&(
    <div className="popup-cart">
        <div className="overlay"></div>
        <div className="cart-container">
        <div className="cart-row">
            <div className="cart-content">
                <div className="cart-header">
                    <h2 className="cart-title">Car Rental Center</h2>
                    <button className="close-popup" onClick={toggleCartPopup}>Close</button>
                </div>
                <div className="cart-card-container">
                        {cartItems.length === 0 ? (
                            <p>Your cart is empty</p>
                        ) : (
                        <div className="cart-item-card-container">
                            <div>
                                <table>
                                    <thead>
                                    <tr>
                                        <th className="cart_table_item">Image</th>
                                        <th className="cart_table_item">Name</th>
                                        <th className="cart_table_item">Price Per Day</th>
                                        <th className="cart_table_item">Reservation Days</th>
                                        <th className="cart_table_item">Action</th>
                                    </tr>
                                    </thead>
                                    <tbody>{tableRows}</tbody>
                                </table>
                            </div>
                        </div>
                        )}
                    <div className="cart-footer">
                            <div></div>
                            <div></div>
                            <button onClick={checkout}  disabled={isCartEmpty} style={{ backgroundColor: isCartEmpty ? "grey" : "white" }}>Checkout</button>
                    </div>
                </div>
            </div>
        </div>
        </div>
    </div>
    )}

    {/* CheckOut-popup */}
    {showForm && (
  <div className="pop-form">
    <div className="overlay"></div>
    <div className="form-container">
      <div className="form-content">
        <div className="cart-header">
          <h2 className="form-header-title">Check Out</h2>
          <button className="close-popup" onClick={checkout}>
            Close
          </button>
        </div>
        <form className="order-form" onSubmit={placeOrder}>
          {formError && (
            <p className="error-message">Please fill in all fields.</p>
          )}
          <div className="form-table-container">
            <div className="form-row">
              <div className="form-col">
                <label htmlFor="firstName">First Name:</label>
                <input
                  type="text"
                  name="firstName"
                  id="firstName"
                  value={firstName}
                  onChange={handleChange}
                />
              </div>
              <div className="form-col">
                <label htmlFor="lastName">Last Name:</label>
                <input
                  type="text"
                  name="lastName"
                  id="lastName"
                  value={lastName}
                  onChange={handleChange}
                />
              </div>
            </div>
            <div className="form-row">
              <div className="form-col">
                <label htmlFor="address">Address Line:</label>
                <input
                  type="text"
                  name="address"
                  id="address"
                  value={address}
                  onChange={handleChange}
                />
              </div>
              <div className="form-col">
                <label htmlFor="paymentType">Payment Type:</label>
                <select
                  name="paymentType"
                  id="paymentType"
                  onChange={handleChange}
                >
                  <optgroup label="paymentType">
                    <option value="master">Master</option>
                    <option value="visa">Visa</option>
                  </optgroup>
                </select>
              </div>
            </div>
            <div className="form-row">
              <div className="form-col">
                <label htmlFor="email">Email:</label>
                <input
                  type="email"
                  name="email"
                  id="email"
                  value={email}
                  onChange={handleChange}
                />
              </div>
            </div>
          </div>
          <div className="cart-footer">
            <div className="order-form-price">
              <h3>Total Price: ${totalPrice.toFixed(2)}</h3>
            </div>
            <button type="submit" onClick={checkout}>
              Continue Selection
            </button>
            <button type="submit" onClick={placeOrder}>
              Place Order
            </button>
          </div>
        </form>
      </div>
    </div>
  </div>
)}

    {showForm && (
        <div className="pop-form">
            <div className="overlay"></div>
                <div className="form-container">
                    <div className="form-content">
                        <div className="cart-header">
                            <h2 className="form-header-title">Check Out</h2>
                            <button className="close-popup" onClick={checkout}>Close</button>
                        </div>
                        <form className="order-form" onSubmit={placeOrder}>
                            {formError && <p className="error-message">Please fill in all fields.</p>}
                        <div className="form-table-container">
                            <div className="order-form-item">
                                <label htmlFor="name">First Name: </label>
                                    <input
                                        type="text"
                                        name="firstName"
                                        id="firstName"
                                        value={firstName}
                                        onChange={handleChange}
                                    />
                            </div>

                            <div className="order-form-item">
                                <label htmlFor="name">Last Name: </label>
                                    <input
                                        type="lastName"
                                        name="lastName"
                                        id="lastName"
                                        value={lastName}
                                        onChange={handleChange}
                                    />
                            </div>

                            <div className="order-form-item">
                                <label>Address Line: </label>
                                    <input
                                        type="text"
                                        name="address"
                                        id="address"
                                        value={address}
                                        onChange={handleChange}
                                    />
                            </div>

                            <div className="order-form-item">
                                <label>Suburb: </label>
                                    <input
                                        type="text"
                                        name="suburb"
                                        id="suburb"
                                        value={suburb}
                                        onChange={handleChange}
                                    />
                            </div>

                            <div className="order-form-item">
                                <label>City: </label>
                                    <input
                                        type="text"
                                        name="city"
                                        id="city"
                                        value={city}
                                        onChange={handleChange}
                                    />
                            </div>

                            <div className="order-form-item">
                                <label>State: </label>
                                    <input
                                        type="text"
                                        name="state"
                                        id="state"
                                        value={state}
                                        onChange={handleChange}
                                    />
                            </div>

                            <div className="order-form-item">
                                <label>Post Code: </label>
                                    <input
                                        type="text"
                                        name="postCode"
                                        id="postCode"
                                        value={postCode}
                                        onChange={handleChange}
                                    />
                            </div>

                            <div className="order-form-item">
                                <label>Payment Type: </label>
                                <select name="paymentType" id="paymentType" onChange={handleChange}>
                                    <optgroup label="paymentType">
                                        <option value="master">Master</option>
                                        <option value="visa">Visa</option>
                                    </optgroup>
                                </select>   
                            </div>

                            <div className="order-form-item">
                                <label>Email: </label>
                                    <input
                                        type="email"
                                        name="email"
                                        id="email"
                                        value={email}
                                        onChange={handleChange}
                                    />
                            </div>
                        </div>
                        </form>
                    <div className="cart-footer">
                    <div className="order-form-price"><h3>Total Price: ${totalPrice.toFixed(2)}</h3></div>

                            <button type="submit" onClick={checkout}>Continue Selection</button>
                            <button type="submit" onClick={placeOrder}>Place Order</button>     
                    </div>
                </div>
            </div>
        </div>
    )}
</body>
</>
)
}

export default App;
