import logo from "/Users/net/car-rental-system/src/assets/images/logo.png";
import "./App.css";
import React, { useState } from "react";
import itemList from "/Users/net/car-rental-system/src/components/itemList.js";


function App() {
    const [data, setData] = useState(itemList)
    const [popup, setPopup] = useState(false)
    const [selectedItem, setSelectedItem] = useState(null);
    const [cartItems, setCartItems] = useState([]);
    const [totalPrice, setTotalPrice] = useState(0); 
    const [isCartEmpty, setIsCartEmpty] = useState(true);
    const [showForm, setShowForm] = useState(false);
    const [searchTerm, setSearchTerm] = useState("");
    const [cartPopup, setCartPopup] = useState(false);
    const [searchResults, setSearchResults] = useState([]);

        const [name, setName] = useState("");
        const [address, setAddress] = useState("");
        const [suburb, setSuburb] = useState("");
        const [state, setState] = useState("");
        const [country, setCountry] = useState("");
        const [phone, setPhone] = useState("");
        const [email, setEmail] = useState("");
        const [formError, setFormError] = useState(false);


    const filterItems = (Item) => {
        const result = itemList.filter((curData)=>{
            return curData.subcategory===Item; 
        })
        setData(result);
    };

    const handleSearchChange = (event) => {
        setSearchTerm(event.target.value);
    };


    const handleSearch = (e) => {
        e.preventDefault();
        const result = itemList.filter((item) => {
            return item.name.includes(searchTerm);
            })
        setSearchResults(result);
    };

    const togglePopup = (item) => {
        setSelectedItem(item);
        setPopup(!popup);
    }

    const toggleCartPopup = () => {
        setCartPopup(!cartPopup);
    }

    const closeSearchResultsPopup = () => {
        setSearchResults([]);
    }

    const addToCart = (item) => {
        if (item.quantity > 0){
        setCartItems([...cartItems, item]);
        setTotalPrice(totalPrice + item.price);
        setIsCartEmpty(false);
        }
    }

    const removeItem = (item) => {
        const itemIndex = cartItems.findIndex((i) => i === item);
        if (itemIndex >= 0) {
        const updatedItems = [...cartItems];
        updatedItems.splice(itemIndex, 1);

        setCartItems(updatedItems);
        setTotalPrice(totalPrice - item.price);
        setIsCartEmpty(updatedItems.length === 0);
        }
        };

    const clearCart = () => {
        setCartItems([]);
        setTotalPrice(0);
        setIsCartEmpty(true);
    };

    const checkout = (item) => {
        if (!isCartEmpty) {
        setShowForm(!showForm);
        setCartPopup(false);
        setCartItems([]);
        }
    };

    const placeOrder = (event) => {
        event.preventDefault();
    if (name && address && suburb && state && country && phone && email !== " ") {
        setShowForm(false);
        setCartItems([]);
        setIsCartEmpty(true);
        setFormError(true);

    // Handle form submission here, e.g. submit data to a server or show a confirmation message
        console.log("Form submitted", name, address, suburb, state, country, phone, email);
        setShowForm(false);
        setName("");
        setAddress("");
        setSuburb("");
        setState("");
        setCountry("");
        setPhone("");
        setEmail("");
        setFormError(false);
        alert("Order placed successfully!");
  };
};

    return (
    <>
    {/* NavBar */}
