// export default filterItems;

import React, { useState, useEffect } from "react";
import axios from "axios";

function filter() {
  const [error, setError] = useState(null);
  const [isLoaded, setIsLoaded] = useState(false);
  const [cars, setCars] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState("");
  const [filteredCars, setFilteredCars] = useState([]);

  useEffect(() => {
    fetchCars();
  }, []);

  const fetchCars = () => {
    axios
      .get("http://localhost:3001/cars.json")
      .then((response) => {
        setIsLoaded(true);
        setCars(response.data.cars);
        setFilteredCars(response.data.cars);
      })
      .catch((error) => {
        setIsLoaded(true);
        setError(error);
      });
  };

  const handleCategoryChange = (event) => {
    const selectedCategory = event.target.value;
    setSelectedCategory(selectedCategory);

    if (selectedCategory === "") {
      setFilteredCars(cars);
    } else {
      const filteredCars = cars.filter((car) => car.seats === selectedCategory);
      setFilteredCars(filteredCars);
    }
  };

  if (error) {
    return <div>Error: {error.message}</div>;
  } else if (!isLoaded) {
    return <div>Loading...</div>;
  } else {
    return (
      <div>
        <select value={selectedCategory} onChange={handleCategoryChange}>
          <option value="">All</option>
          <option value="2">2 Seats</option>
          <option value="4">4 Seats</option>
          <option value="5">5 Seats</option>
          <option value="7">7 Seats</option>
          <option value="8">8 Seats</option>
        </select>

        <div className="mainArea-card-container">
          {filteredCars.map((car) => (
            <div className="mainArea-card-item" key={car.id}>
              {/* Car details */}
            </div>
          ))}
        </div>
      </div>
    );
  }
}

export default CarBrowsing;
