import logo from "/Users/net/car-rental-system/src/assets/images/logo.png";
import "./App.css";
import React, { useState } from "react";
import cars from "/Users/net/car-rental-system/src/components/cars.json";


function App() {
    const [data, setData] = useState(cars)
    const [cartItems, setCartItems] = useState([]);
    const [totalPrice, setTotalPrice] = useState(0); 
    const [isCartEmpty, setIsCartEmpty] = useState(true);
    const [showForm, setShowForm] = useState(false);
    const [cartPopup, setCartPopup] = useState(false);
    const [reservationDays, setReservationDays] = useState(1);


        const [name, setName] = useState("");
        const [address, setAddress] = useState("");
        const [suburb, setSuburb] = useState("");
        const [state, setState] = useState("");
        const [country, setCountry] = useState("");
        const [phone, setPhone] = useState("");
        const [email, setEmail] = useState("");
        const [formError, setFormError] = useState(false);

    const handleChange = (e) => {
        setReservationDays(e.target.value);
        if (+e){

        }
        const updatedCartItems = [...cartItems];
        const itemIndex = updatedCartItems.findIndex((item) => item.id === itemId);
        updatedCartItems[itemIndex] = {
            ...updatedCartItems[itemIndex],
            reservationDays: updatedCartItems[itemIndex].reservationDays + 1,
        };
        setCartItems(updatedCartItems); 

      };

    const tableRows = cartItems.map((item) => (
        <tr key={item.id}>
            <td className="cart_table_item">
                <img src={item.image} alt={item.name} height="50" width="80" />
            </td>
            <td className="cart_table_item">{item.name}</td>
            <td className="cart_table_item">{item.price_per_day}</td>
            <td className="cart_table_item">
            <input
                type="number"
                min="1"
                value={reservationDays}
                onChange={handleChange}
            />
            </td>
            <td className="cart_table_item">
            <button onClick={() => removeItem(item)}>Remove</button>
            </td>
        </tr>
    ));


    const increaseItem = (itemId) => {
        const updatedCartItems = [...cartItems];
        const itemIndex = updatedCartItems.findIndex((item) => item.id === itemId);
        updatedCartItems[itemIndex] = {
            ...updatedCartItems[itemIndex],
            reservationDays: updatedCartItems[itemIndex].reservationDays + 1,
        };
        setCartItems(updatedCartItems);
    };

    const decreaseItem = (itemId) => {
        const updatedCartItems = [...cartItems];
        const itemIndex = updatedCartItems.findIndex((item) => item.id === itemId);
        if (updatedCartItems[itemIndex].reservationDays > 1) {
            updatedCartItems[itemIndex] = {
                ...updatedCartItems[itemIndex],
                reservationDays: updatedCartItems[itemIndex].reservationDays - 1,
            };
            setCartItems(updatedCartItems);
        }
    };


    const addToCart = (item) => {
        if (item.availability = "True"){
        setCartItems([...cartItems, item]);
        setTotalPrice(totalPrice + item.price_per_day);
        setIsCartEmpty(false);
        }
        console.log(cartItems)
    }

    const toggleCartPopup = () => {
        setCartPopup(!cartPopup);
    }

    const removeItem = (item) => {
        const itemIndex = cartItems.findIndex((i) => i === item);
        if (itemIndex >= 0) {
        const updatedItems = [...cartItems];
        updatedItems.splice(itemIndex, 1);

        setCartItems(updatedItems);
        setTotalPrice(totalPrice - item.price_per_day);
        setIsCartEmpty(updatedItems.length === 0);
        }
        };

    const checkout = (item) => {
        if (!isCartEmpty) {
        setShowForm(!showForm);
        setCartPopup(false);
        setCartItems([]);
        }
    };

    const placeOrder = (event) => {
        event.preventDefault();
    if (name && address && suburb && state && country && phone && email !== " ") {
        setShowForm(false);
        setCartItems([]);
        setIsCartEmpty(true);
        setFormError(true);

    // Handle form submission here, e.g. submit data to a server or show a confirmation message
        console.log("Form submitted", name, address, suburb, state, country, phone, email);
        setShowForm(false);
        setName("");
        setAddress("");
        setSuburb("");
        setState("");
        setCountry("");
        setPhone("");
        setEmail("");
        setFormError(false);
        alert("Order placed successfully!");
  };
};

    return (
    <>
    {/* NavBar */}
    <nav class='navBar-setting navBar-menu-text nav-container'>
        <div class="nav-container-item"><img class='logo' src={logo} alt='Logo'/></div>
        <div class="shoppingCart-item"><h1>Car Rental Center</h1></div> 
        <botton class="reservation-btn" onClick={toggleCartPopup}>Car Reservation ({cartItems.length})</botton>
    </nav>

    <body>
     {/* main-area */}
        <div class="main-area">
            <div class="mainArea-card-container">
                {data.map((values) => {
                    const {id,name,mileage,image,fuel_type,seats, price_per_day,availability,description}=values;
                    return(
                        <>
                        <div className="mainArea-card" key={id}>
                            <img className= "mainArea-img" src={image}></img>
                            <h3 className="mainArea-name">{name}</h3>
                            <p className="mainArea-title"><b>mileage: </b>{mileage} kms</p>
                            <p className="mainArea-title"><b>fuel_type: </b>{fuel_type}</p> 
                            <p className="mainArea-title"><b>seats: </b>{seats}</p>
                            <p className="mainArea-title"><b>price_per_day: </b>{price_per_day} AU</p>
                            <p className="mainArea-title"><b>availability: </b>{availability}</p>
                            <p className="mainArea-description"><b>description: </b>{description}</p>
                            <br/>
                            <div>
                                <button className="addToCartBtn" onClick={() => addToCart(values)}>Add to Cart</button>
                            </div>
                        </div>
                        </>
                        )
                    })}
            </div>
        </div>

    {/* cart-popup */}
    {cartPopup &&(
    <div className="popup-cart">
        <div className="overlay"></div>
        <div className="cart-container">
        <div className="cart-row">
            <div className="cart-content">
                <div className="cart-header">
                    <h2 className="cart-title">Car Rental Center</h2>
                    <button className="close-popup" onClick={toggleCartPopup}>Close</button>
                </div>
                <div className="cart-card-container">
                        {cartItems.length === 0 ? (
                            <p>Your cart is empty</p>
                        ) : (
                        <div className="cart-item-card-container">
                            <div>
                                <table>
                                    <thead>
                                    <tr>
                                        <th className="cart_table_item">Image</th>
                                        <th className="cart_table_item">Name</th>
                                        <th className="cart_table_item">Price</th>
                                        <th className="cart_table_item">Reservation Days</th>
                                        <th className="cart_table_item">Action</th>
                                    </tr>
                                    </thead>
                                    <tbody>{tableRows}</tbody>
                                </table>
                            </div>
                        </div>
                        )}
                    <div className="cart-footer">
                            <h3>Total Price: ${totalPrice.toFixed(2)}</h3>
                            <div></div>
                            <button onClick={checkout}  disabled={isCartEmpty} style={{ backgroundColor: isCartEmpty ? "grey" : "white" }}>Checkout</button>
                    </div>
                </div>
            </div>
        </div>
        </div>
    </div>
    )}

    {/* CheckOut-popup */}
    {showForm && (
        <div className="pop-form">
            <div className="overlay"></div>
                <div className="form-container">
                    <div className="form-content">
                        <div className="cart-header">
                            <h2 className="form-header-title">Check Out</h2>
                            <button className="close-popup" onClick={checkout}>Close</button>
                        </div>
                    <div className="form-table-container">
                        <form className="order-form" onSubmit={placeOrder}>
                            {formError && <p className="error-message">Please fill in all fields.</p>}

                        <div className="order-form-item">
                            <label htmlFor="name">First Name: </label>
                                <input
                                    type="text"
                                    name="name"
                                    id="name"
                                    value={name}
                                    onChange={(event) => setName(event.target.value)}
                                />
                        </div>

                        <div className="order-form-item">
                            <label htmlFor="name">Last Name: </label>
                                <input
                                    type="text"
                                    name="name"
                                    id="name"
                                    value={name}
                                    onChange={(event) => setName(event.target.value)}
                                />
                        </div>

                        <div className="order-form-item">
                            <label>Address Line: </label>
                                <input
                                    type="text"
                                    name="address"
                                    id="address"
                                    value={address}
                                    onChange={(event) => setAddress(event.target.value)}
                                />
                        </div>

                        <div className="order-form-item">
                            <label>City: </label>
                                <input
                                    type="text"
                                    name="suburb"
                                    id="suburb"
                                    value={suburb}
                                    onChange={(event) => setSuburb(event.target.value)}
                                />
                        </div>

                        <div className="order-form-item">
                            <label>State: </label>
                                <input
                                    type="text"
                                    name="state"
                                    id="state"
                                    value={state}
                                    onChange={(event) => setState(event.target.value)}
                                />
                        </div>

                        <div className="order-form-item">
                            <label>Post Code: </label>
                                <input
                                    type="text"
                                    name="country"
                                    id="country"
                                    value={country}
                                    onChange={(event) => setCountry(event.target.value)}
                                />
                        </div>

                        <div className="order-form-item">
                            <label>Payment Type: </label>
                            <select name="paymentType" id="paymentType" onChange={(event) => setPhone(event.target.value)}>
                                <optgroup label="paymentType">
                                    <option value="master">Master</option>
                                    <option value="visa">Visa</option>
                                </optgroup>
                            </select>   
                        </div>

                        <div className="order-form-item">
                            <label>Email: </label>
                                <input
                                    type="email"
                                    name="email"
                                    id="email"
                                    value={email}
                                    onChange={(event) => setEmail(event.target.value)}
                                />
                        </div>

                        </form>
                    </div>
                    <div className="cart-footer">
                    <div></div>
                            <button type="submit" onClick={placeOrder}>Continue Selection</button>
                            <button type="submit" onClick={placeOrder}>Place Order</button>     
                    </div>
                </div>
            </div>
        </div>
    )}
</body>
</>
)
}

export default App;
